// Agent Decision Node
// Examines lead/message status and decides which MCP endpoint to call

const items = $input.all();
const output = [];

for (const item of items) {
  const leads = item.json.leads || {};
  const messages = item.json.messages || {};

  // Decision logic based on status
  let nextAction = null;
  let endpoint = null;
  let params = {};

  // Priority 1: Send approved messages
  if (messages.APPROVED && messages.APPROVED > 0) {
    nextAction = 'send_messages';
    endpoint = 'http://localhost:8001/tools/send_messages';
    params = { use_queue: true, batch_size: 50, dry_run: false };
  }
  // Priority 2: Review pending messages
  else if (messages.PENDING && messages.PENDING > 0) {
    nextAction = 'review_messages';
    endpoint = 'http://localhost:8001/tools/review_messages';
    params = { auto_approve: false, min_quality_score: 70 };
  }
  // Priority 3: Generate messages for enriched leads
  else if (leads.ENRICHED && leads.ENRICHED > 0) {
    nextAction = 'generate_messages';
    endpoint = 'http://localhost:8001/tools/generate_messages';
    params = { limit: 50 };
  }
  // Priority 4: Enrich generated leads
  else if (leads.GENERATED && leads.GENERATED > 0) {
    nextAction = 'enrich_leads';
    endpoint = 'http://localhost:8001/tools/enrich_leads';
    params = { mode: 'offline', limit: 50 };
  }
  // Priority 5: Generate new leads if needed
  else if ((!leads.NEW || leads.NEW < 10) && (!leads.GENERATED || leads.GENERATED < 10)) {
    nextAction = 'generate_leads';
    endpoint = 'http://localhost:8001/tools/generate_leads';
    params = { count: 100, save_to_db: true };
  }
  // No action needed
  else {
    nextAction = 'wait';
    endpoint = null;
  }

  output.push({
    json: {
      ...item.json,
      agent_decision: {
        action: nextAction,
        endpoint: endpoint,
        params: params,
        timestamp: new Date().toISOString()
      }
    }
  });
}

return output;

// No action needed, log and continue
const items = $input.all();
return items.map(item => ({
  json: {
    status: 'waiting',
    message: 'No leads need processing',
    timestamp: new Date().toISOString()
  }
}));

// Log the result
const items = $input.all();
const results = [];

for (const item of items) {
  console.log('MCP Tool Result:', JSON.stringify(item.json, null, 2));
  results.push({
    json: {
      success: item.json.success || false,
      action: item.json.action,
      details: item.json,
      timestamp: new Date().toISOString()
    }
  });
}

return results;
